// import { useHistory } from "react-router-dom";
import React, { useState, useEffect } from "react";
import { useHistory } from "react-router-dom";
import "./Login.css";

// const history = useHistory();

function Login() {
  const [state, setState] = useState({
    username: "",
    password: "",
    isLoggedIn: false,
    error: "",
    teacherData: null, // Initialize teacherData as null
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setState({ ...state, [name]: value });
  };

  const handleSubmit = async (e) => {
    // console.log(data);
    e.preventDefault();
    const username = e.target.username.value;
    const password = e.target.password.value;
    console.log(username, password);
    try {
      const response = await axios.post("/api/login/", {
        username: username,
        password: password,
      });
      if (response.status === 200) {
        // Login successful
        // Redirect to /dashboard and pass session data
        console.log(response.data);
        history.push("/dashboard", { sessionData: response.data });
      }
    } catch (error) {
      // Handle login error
    }
  };
  const history = useHistory();
  return (
    <div className="login-container">
      <h2>Login</h2>
      {error && <div style={{ color: "red" }}>{error}</div>}
      {teacherData && (
        <div>
          <p>Teacher Name: {teacherData.teacher.name}</p>
          {/* Display other teacher data as needed */}
        </div>
      )}
      <form onSubmit={handleSubmit} className="login-form">
        <div>
          <label>Username:</label>
          <input
            type="text"
            name="username"
            value={username}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <label>Password:</label>
          <input
            type="password"
            name="password"
            value={password}
            onChange={handleInputChange}
          />
        </div>
        <div>
          <button type="submit">Login</button>
        </div>
      </form>
    </div>
  );
}

export default Login;
