import {
  Accordion,
  AccordionButton,
  AccordionIcon,
  AccordionItem,
  AccordionPanel,
  Avatar,
  Box,
  Button,
  Divider,
  Flex,
  FormControl,
  FormLabel,
  HStack,
  Image,
  Input,
  Popover,
  PopoverArrow,
  PopoverBody,
  PopoverCloseButton,
  PopoverContent,
  PopoverHeader,
  PopoverTrigger,
  Text,
  Textarea,
  VStack,
} from "@chakra-ui/react";
import React, { useEffect, useState } from "react";
import axios from "axios";
import Calendar from "react-calendar";
import { Link } from "react-router-dom";
import "./Calendar.css";
import illustration_A from "./../../assets/images/illustration_A.png";
import illustration_B from "./../../assets/images/illustration_B.png";
import illustration_C from "./../../assets/images/illustration_C.png";
import { DownloadIcon } from "@chakra-ui/icons";

function Dashboard() {
  const usertoken = localStorage.getItem("usernameToken");
  const sidtoken = parseInt(localStorage.getItem("sidToken"));

  const [user, setUser] = useState([]);
  const [data, setData] = useState([]);
  const [courses, setCourses] = useState([]);
  const [selectedCourse, setSelectedCourse] = useState(null);
  const [assignment, setAssignment] = useState([]);
  const [notes, setNotes] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (sidtoken != null) {
          const getUser = await axios.get(
            `http://localhost:8000/api/temp/?sid=${sidtoken}`
          );
          setUser(getUser.data[0]);
          const response = await axios.get(
            `http://localhost:8000/api/courses/?sid=${sidtoken}`
          );
          setCourses(response.data);
        }
      } catch (error) {
        // console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);
  useEffect(() => {
    const fetchData = async () => {
      try {
        if (selectedCourse != null) {
          const assignment = await axios.get(
            `http://localhost:8000/api/assignments/?id=${selectedCourse}`
          );
          setAssignment(assignment.data);
          const notes = await axios.get(
            `http://localhost:8000/api/notes/?id=${selectedCourse}`
          );
          setNotes(notes.data);
        }
      } catch (error) {
        console.error("Error fetching data:", error);
      }
    };
    fetchData();
  }, [selectedCourse]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (sidtoken != null) {
          const notes_back = await axios.get(
            `http://localhost:8000/api/shownotes/`
          );
          console.log(notes_back.data);
          setNotes(notes_back.data);
          const assignment_back = await axios.get(
            `http://localhost:8000/api/showassignment/`
          );
          console.log(assignment_back.data);
          setAssignment(assignment_back.data);
        }
      } catch (error) {
        // console.error("Error fetching data:", error);
      }
    };

    fetchData();
  }, []);

  const handleCourseSelect = (id) => {
    console.log(id);
    setSelectedCourse(id);
  };

  const [assignmnetFormData, setAssignmentFormData] = useState({
    title: "",
    description: "",
    file: null,
  });

  const [notesFormData, setNotesFormData] = useState({
    title: "",
    description: "",
    file: null,
  });

  const handleChangeAssignment = (e) => {
    const { name, value } = e.target;
    setAssignmentFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };
  const handleChangeNotes = (e) => {
    const { name, value } = e.target;
    setNotesFormData((prevData) => ({
      ...prevData,
      [name]: value,
    }));
  };

  const handleAssignmentSubmit = async (e) => {
    e.preventDefault();
    // Perform any action with the form data, like sending it to an API
    const formData = new FormData();
    formData.append("title", assignmnetFormData.title);
    formData.append("file", assignmnetFormData.file);
    try {
      const response = await axios.post(
        "http://localhost:8000/api/assignmentupload/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data", // Set the content type to multipart/form-data
          },
        }
      );
      console.log("Response data assignment:", response.data);
      // Handle the response data as needed
    } catch (error) {
      console.error("Error:", error);
      // Handle errors here
    }
    console.log("Form Data:", assignmnetFormData);
  };
  const handleNotesSubmit = async (e) => {
    e.preventDefault();
    const formData = new FormData();
    formData.append("title", notesFormData.title);
    formData.append("file", notesFormData.file);

    try {
      const response = await axios.post(
        "http://localhost:8000/api/notesupload/",
        formData,
        {
          headers: {
            "Content-Type": "multipart/form-data", // Set the content type to multipart/form-data
          },
        }
      );
      console.log("Response data notes:", response.data);
      // Handle the response data as needed
    } catch (error) {
      console.error("Error:", error);
      // Handle errors here
    }
    // console.log("Form Data:", assignmnetFormData);
  };

  return (
    <>
      <Flex
        position={"fixed"}
        bottom={0}
        top={110}
        w={"full"}
        bg={"whitesmoke"}
      >
        <Box minW={"64px"} maxW={"64px"} bg={"#d8dcf0"}></Box>
        <VStack minW={250} maxW={250} p={3} bg={"white"}>
          <HStack padding={3} w={"full"} bg={"#ebedf7"} borderRadius={"2xl"}>
            <Popover>
              <PopoverTrigger>
                <Avatar cursor={"pointer"} name={user.name}></Avatar>
              </PopoverTrigger>
              <PopoverContent w={200}>
                <PopoverArrow />
                <PopoverCloseButton />
                <PopoverBody>
                  <br />
                  <Button w={"full"} colorScheme="facebook">
                    My Profile
                  </Button>
                  <Divider my={2} />
                  <Button w={"full"} colorScheme="telegram">
                    Logout
                  </Button>
                </PopoverBody>
              </PopoverContent>
            </Popover>
            <Text>{user.name}</Text>
          </HStack>

          <Divider borderColor={"blackAlpha.300"} my={1} />

          {courses.map((item) => (
            <Button
              w={"full"}
              justifyContent={"flex-start"}
              flexWrap={"wrap"}
              overflow={"hidden"}
              borderRadius={"full"}
              activeStyle={{ color: "red", border: "2px solid red" }}
              key={item.id}
              onClick={() => handleCourseSelect(item.cid)}
            >
              {item.name}
            </Button>
          ))}

          <Divider borderColor={"blackAlpha.300"} my={1} />

          <Button
            w={"full"}
            justifyContent={"flex-start"}
            borderRadius={"full"}
            activeStyle={{ color: "red" }}
          >
            Announcements
          </Button>
          <Button
            w={"full"}
            justifyContent={"flex-start"}
            borderRadius={"full"}
            activeStyle={{ color: "red" }}
          >
            Alert Messages
          </Button>
          <Button
            w={"full"}
            justifyContent={"flex-start"}
            borderRadius={"full"}
            activeStyle={{ color: "red" }}
          >
            Manage Events
          </Button>
          <Button
            w={"full"}
            justifyContent={"flex-start"}
            borderRadius={"full"}
            activeStyle={{ color: "red" }}
          >
            List Students
          </Button>
          <Button
            w={"full"}
            justifyContent={"flex-start"}
            borderRadius={"full"}
            activeStyle={{ color: "red" }}
          >
            Manage Tutorials
          </Button>
        </VStack>
        <Flex flexGrow={1} position={"relative"}>
          <Flex flexGrow={1} justify={"space-evenly"} gap={"24px"} p={"16px"}>
            {/* <Box>
                <Image src={illustration_A} opacity={1} />
                <Text textAlign={'justify'} mt={-10} bg={'#ebedf7'} fontSize={14} p={3} borderRadius={10}>
                  Lorem ipsum dolor sit amet consectetur adipisicing elit. Minima, autem!
                  Lorem ipsum dolor sit ame
                </Text>

              </Box>

              <Box>
                <Image src={illustration_B} opacity={1} />
