function Login() {
  const [state, setState] = useState({
    username: "",
    password: "",
    isLoggedIn: false,
    error: "",
    teacherData: null, // Initialize teacherData as null
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setState({ ...state, [name]: value });
